
(()=>{const logout=document.querySelectorAll('.menu__item');logout.forEach((item)=>{if(item.pathname==='/auth/logout'){item.addEventListener('click',async()=>{await fetch('/settings/close',{method:'post'}).then((response)=>{console.log('response ',response);return response.json;}).then((resp)=>console.log('resp: ',resp));});}});})();

(()=>{const inputs=document.querySelectorAll('input');inputs.forEach((textField)=>{textField.addEventListener('keyup',(event)=>{let label=textField.previousElementSibling;if(event.target.value==='')
label.classList.remove('active','highlight');else
label.classList.add('active','highlight');});textField.addEventListener('blur',(event)=>{let label=textField.previousElementSibling;if(event.target.value==='')
label.classList.remove('active','highlight');else
label.classList.remove('highlight');});textField.addEventListener('focus',(event)=>{let label=textField.previousElementSibling;if(event.target.value==='')
label.classList.remove('highlight');else
label.classList.add('highlight');});});})();